import{_ as t,f as p}from"./app.3e676b78.js";const a={},o=p('<h1 id="_2020\u9AD8\u8003\u8BED\u6587\u5907\u8003\u65B9\u6848" tabindex="-1"><a class="header-anchor" href="#_2020\u9AD8\u8003\u8BED\u6587\u5907\u8003\u65B9\u6848" aria-hidden="true">#</a> 2020\u9AD8\u8003\u8BED\u6587\u5907\u8003\u65B9\u6848</h1><p>\u73B0\u4EE3\u6587\u9605\u8BFB\u7740\u91CD\u8003\u67E5\u5C0F\u7EB8\u6761\u4EEC\u7B5B\u9009\u548C\u63D0\u53D6\u4FE1\u606F\u7684\u80FD\u529B\uFF0C\u7406\u89E3\u548C\u5206\u6790\u7684\u80FD\u529B\uFF0C\u9274\u8D4F\u548C\u8BC4\u4EF7\u7684\u80FD\u529B\u3002\u5728\u6700\u540E\u7684100\u5929\u4E2D\uFF0C\u5C0F\u7EB8\u6761\u4EEC\u53EF\u4EE5\u5206\u522B\u5BF9\u8BBA\u8FF0\u7C7B\u3001\u6587\u5B66\u7C7B\u3001\u5B9E\u7528\u7C7B\u6587\u672C\u7684\u4E0D\u540C\u9898\u578B\u8FDB\u884C\u603B\u7ED3\u548C\u8BAD\u7EC3\u3002</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-27/4251d466ffe24761a1d30bd1580241f2.jpg" alt="img" loading="lazy"></p><p>\u8BBA\u8FF0\u7C7B\u6587\u672C\u5F80\u5F80\u4EE5\u9009\u62E9\u9898\u5F62\u5F0F\u51FA\u73B0\uFF0C\u5C0F\u7EB8\u6761\u4EEC\u9700\u8981\u57F9\u517B\u7684\u662F\u5FEB\u901F\u4ECE\u5927\u91CF\u4FE1\u606F\u4E2D\u63D0\u53D6\u201C\u5E72\u8D27\u201D\u7684\u80FD\u529B\uFF0C\u56E0\u800C\u5E73\u65F6\u9700\u8981\u591A\u4F5C\u201C\u52FE\u753B\u5173\u952E\u8BCD\u8BED\uFF0C\u51C6\u786E\u56DE\u6587\u5BF9\u6BD4\u201D\u7684\u8BAD\u7EC3\u3002</p><p>https://zwzt-h5.zuowenzhitiao.com/share/long.html?v=0x25030640&amp;type=2&amp;articleId=T0RBMU9B</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-01-02/ecb14b69d7354065aff54e496d7d5627.jpg" alt="img" loading="lazy"></p><p>\u6587\u5B66\u7C7B\u6587\u672C\u4E00\u822C\u4F1A\u8003\u67E5\u7B80\u7B54\u9898\u6216\u5206\u6790\u9274\u8D4F\u9898\uFF0C\u5728\u51B2\u523A\u9636\u6BB5\uFF0C\u5E94\u9488\u5BF9\u5F80\u5E74\u771F\u9898\u7684\u51FA\u9898\u65B9\u5F0F\u603B\u7ED3\u51FA\u7B54\u9898\u201C\u5957\u8DEF\u201D\u3002\u8B6C\u5982\u5728\u5206\u6790\u5C0F\u8BF4\u4EBA\u7269\u5F62\u8C61\u65F6\uFF0C\u5E94\u8BE5\u5148\u603B\u4F53\u6982\u62EC\u4EBA\u7269\u7684\u8EAB\u4EFD\u5730\u4F4D\u3001\u5916\u5728\u7279\u5F81\uFF0C\u518D\u7ED3\u5408\u5177\u4F53\u8BED\u53E5\uFF0C\u9010\u4E00\u7B80\u6790\u4EBA\u7269\u5185\u5728\u7279\u70B9\u7B49\u7B49\u3002\u6B64\u5916\uFF0C\u4E5F\u53EF\u4EE5\u6839\u636E\u5185\u5BB9\u3001\u4E3B\u9898\u3001\u7ED3\u6784\u3001\u8BED\u8A00\u8FD9\u56DB\u5927\u8981\u7D20\uFF0C\u603B\u7ED3\u6563\u6587\u9605\u8BFB\u7684\u7B54\u9898\u8981\u70B9\u3002</p><p>https://zwzt-h5.zuowenzhitiao.com/share/long.html?v=0x25030640&amp;type=2&amp;articleId=TkRFNE13</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2019-05-27/aabdfb1861b543fda119b784879aacde.jpg" alt="img" loading="lazy"></p><p>\u5B9E\u7528\u7C7B\u6587\u672C\u5927\u90FD\u662F\u8003\u67E5\u5728\u793E\u4F1A\u79D1\u5B66\u6216\u81EA\u7136\u79D1\u5B66\u65B9\u9762\u6709\u7A81\u51FA\u8D21\u732E\u7684\u540D\u4EBA\u4F20\u8BB0\uFF0C\u56E0\u800C\u53EF\u4EE5\u603B\u7ED3\u4F20\u8BB0\u7279\u70B9\uFF0C\u5B66\u4E60\u5982\u4F55\u5206\u6790\u4F20\u8BB0\u7ED3\u6784\u3001\u8BC4\u4EF7\u4F20\u4E3B\u4EF7\u503C\u7B49\u7B49\u3002</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-27/93931472788144809e9e7557711d2d12.jpg" alt="img" loading="lazy"></p><p><strong>\u53E4 \u4EE3 \u6587 \u5B66 \u90E8 \u5206</strong></p><p><strong>\u4E00\u3001\u8BFE\u5916\u6587\u8A00\u6587\u90E8\u5206</strong></p><p>\u5C0F\u7EB8\u6761\u4EEC\u9700\u8981\u91CD\u6E29\u521D\u4E2D\u81F3\u9AD8\u4E2D\u6587\u8A00\u6587\u8BFE\u6587\u4E2D\u51FA\u73B0\u8FC7\u7684\u7EA6300\u4E2A\u5B9E\u8BCD\uFF0C\u5E76\u6709\u9488\u5BF9\u6027\u5730\u5C55\u5F00\u7EC3\u4E60\uFF0C\u5982\u575A\u6301\u6BCF\u5929\u9605\u8BFB\u4E00\u7BC7\u8BFE\u5916\u6587\u8A00\u6587\uFF0C\u57F9\u517B\u53E4\u6587\u8BED\u611F\u3002</p><p>https://zwzt-h5.zuowenzhitiao.com/share/long.html?v=0x25030640&amp;type=2&amp;articleId=TkRJd01n</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2019-05-28/a8ec02d462db4ef5a30de7671260ca24.jpg" alt="img" loading="lazy"></p><p>\u5728\u5E73\u65F6\u9605\u8BFB\u4E2D\uFF0C\u8981\u5728\u7406\u89E3\u6587\u7AE0\u57FA\u672C\u5185\u5BB9\u7684\u57FA\u7840\u4E0A\uFF0C\u5F04\u6E05\u695A\u4EBA\u7269\u4E4B\u95F4\u7684\u5173\u7CFB\u53CA\u6587\u4E2D\u91CD\u8981\u4E8B\u4EF6\uFF0C\u8FD9\u4E00\u4E60\u60EF\u6709\u52A9\u4E8E\u6211\u4EEC\u5728\u8003\u573A\u4E0A\u5FEB\u901F\u89E3\u7B54\u9009\u62E9\u9898\u3002\u5728\u8FDB\u884C\u6A21\u62DF\u9898\u8BAD\u7EC3\u65F6\uFF0C\u5219\u5FC5\u987B\u5C06\u95EE\u9898\u4EE3\u5165\u539F\u6587\u8BED\u5883\u3002\u6B64\u5916\uFF0C\u5E94\u603B\u7ED3\u65AD\u53E5\u7684\u4E00\u4E9B\u57FA\u672C\u65B9\u6CD5\uFF0C\u5982\u8BED\u611F\u65AD\u53E5\u6CD5\u3001\u865A\u8BCD\u6807\u5FD7\u6CD5\u7B49\u7B49\u3002\u505A\u7FFB\u8BD1\u9898\u65F6\u8981\u6709\u201C\u6587\u8A00\u610F\u8BC6\u201D\uFF0C\u5207\u52FF\u201C\u5148\u5165\u4E3A\u4E3B\u201D\uFF0C\u8F7B\u7387\u5730\u91C7\u7528\u8BE5\u8BCD\u8BED\u7684\u73B0\u4EE3\u6C49\u8BED\u542B\u4E49\u3002</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-27/88d8ea696ebe4ced83050682f4d1d40b.jpg" alt="img" loading="lazy"></p><p><strong>\u4E8C\u3001\u8BD7\u6B4C\u9274\u8D4F\u90E8\u5206</strong></p><p>\u638C\u63E1\u5FEB\u901F\u5224\u660E\u8BD7\u6B4C\u7C7B\u522B\u7684\u65B9\u6CD5\uFF0C\u5E76\u6839\u636E\u76F8\u5E94\u7C7B\u522B\u8BD7\u6B4C\u7684\u7279\u70B9\u603B\u7ED3\u7B54\u9898\u8981\u70B9\u3002\uFF08\u5982\u501F\u666F\u6292\u60C5\u7C7B\u3001\u6258\u7269\u8A00\u5FD7\u7C7B\u7B49\u7B49\u3002\uFF09\u5728\u5E73\u65F6\u7EC3\u4E60\u4E2D\uFF0C\u4E5F\u8981\u591A\u52A0\u7559\u610F\u6807\u9898\u3001\u610F\u8C61\u3001\u6CE8\u91CA\u7B49\u6587\u5B57\u6750\u6599\uFF0C\u5E76\u6309\u7167\u9898\u578B\u7684\u57FA\u672C\u6A21\u5F0F\u4E0E\u6B65\u9AA4\u603B\u7ED3\u7B54\u9898\u8981\u70B9\u3002\u8B6C\u5982\uFF0C\u9AD8\u8003\u7ECF\u5E38\u8003\u67E5\u201C\u8BD7\u6B4C\u8868\u8FBE\u4E86\u8BD7\u4EBA\u54EA\u4E9B\u60C5\u611F\u201D\u7C7B\u7684\u9898\u578B\uFF0C\u5C0F\u7EB8\u6761\u4EEC\u5C31\u53EF\u4EE5\u5148\u603B\u8BF4\u8BD7\u6B4C\u60C5\u611F\uFF0C\u518D\u7ED3\u5408\u8BD7\u53E5\u9010\u4E00\u9610\u91CA\u8FD9\u4E9B\u60C5\u611F\u3002</p><p>https://zwzt-h5.zuowenzhitiao.com/share/long.html?v=0x25030640&amp;type=2&amp;articleId=T0RBd05R</p><p>https://zwzt-h5.zuowenzhitiao.com/share/long.html?v=0x25030640&amp;type=2&amp;articleId=T0RJNE9B</p><p>https://zwzt-h5.zuowenzhitiao.com/share/long.html?v=0x25030640&amp;type=2&amp;articleId=T0RNeE13</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2019-12-29/1cb4593b8b2840978d9da5601ac9e8cf.jpg" alt="img" loading="lazy"></p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-09/6ced78cb0ebf4a87905a98be3628f0bb.jpg" alt="img" loading="lazy"></p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-15/96c2642946c1426fb0fe27f07eba14cc.jpg" alt="img" loading="lazy"></p><p><strong>\u4E09\u3001\u9ED8\u5199</strong></p><p>\u9010\u4E00\u5BF9\u8003\u7EB2\u89C4\u5B9A\u7BC7\u76EE\u8FDB\u884C\u67E5\u6F0F\u8865\u7F3A\uFF0C\u5708\u753B\u51FA\u9519\u522B\u5B57\u505A\u91CD\u70B9\u590D\u4E60\uFF0C\u7740\u91CD\u6CE8\u610F\u5F02\u4F53\u5B57\u3001\u901A\u5047\u5B57\u3001\u540C\u4E49\u5F02\u5F62\u8BCD\u7684\u5199\u6CD5\uFF0C\u907F\u514D\u51FA\u73B0\u97F3\u5BF9\u5B57\u9519\u7684\u95EE\u9898\u3002\u901A\u8FC7\u5206\u6790\u5386\u5E74\u771F\u9898\uFF0C\u53EF\u4EE5\u53D1\u73B0\u8003\u67E5\u7684\u540D\u53E5\u591A\u51FA\u81EA\u5148\u79E6\u548C\u5510\u5B8B\u4F5C\u54C1\uFF0C\u4E14\u5927\u591A\u4E3A\u5177\u6709\u6DF1\u523B\u601D\u60F3\u610F\u4E49\u3001\u683C\u5F0F\u5BF9\u4ED7\u5DE5\u6574\u6216\u5199\u666F\u53D9\u4E8B\u6781\u4E3A\u7CBE\u5999\u7684\u53E5\u5B50\u3002\u603B\u4E4B\uFF0C\u5728\u767E\u65E5\u5907\u8003\u9636\u6BB5\u4E0D\u80FD\u5149\u80CC\u4E0D\u5199\uFF0C\u800C\u7EC3\u4E60\u65F6\u4E5F\u8981\u529B\u6C42\u4E66\u5199\u5DE5\u6574\u3001\u5B57\u8FF9\u6E05\u695A\u3002</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-27/bec2a08009ce4947862dacd5162e413d.jpg" alt="img" loading="lazy"></p><p><strong>\u8BED \u8A00 \u6587 \u5B57 \u57FA \u7840 \u90E8 \u5206</strong></p><p>\u8BED\u8A00\u6587\u5B57\u5E94\u7528\u90E8\u5206\u9700\u8981\u65E5\u5E38\u8BB0\u5FC6\u548C\u957F\u671F\u79EF\u7D2F\uFF0C\u8B6C\u5982\u590D\u4E60\u75C5\u53E5\u4E13\u9898\u65F6\uFF0C\u5E94\u56DE\u987E\u5E73\u65F6\u6240\u79EF\u7D2F\u7684\u5E38\u89C1\u75C5\u53E5\u6A21\u5F0F\uFF0C\u57F9\u517B\u654F\u611F\u610F\u8BC6\uFF0C\u5FEB\u901F\u627E\u51FA\u642D\u914D\u4E0D\u5F53\u3001\u8BED\u5E8F\u4E0D\u5F53\u7B49\u9519\u8BEF\u3002\u540C\u65F6\uFF0C\u8981\u6478\u6E05\u6210\u8BED\u8868\u8FBE\u7684\u51FA\u9898\u9677\u9631\uFF0C\u5982\u671B\u6587\u751F\u4E49\u3001\u524D\u540E\u77DB\u76FE\u3001\u91CD\u590D\u8D58\u4F59\u7B49\u7B49\u3002\u5728\u5E73\u65F6\u7EC3\u4E60\u4E2D\uFF0C\u8FD8\u53EF\u4EE5\u4ECE\u53E5\u5B50\u6210\u5206\u5165\u624B\uFF0C\u5BF9\u5176\u8FDB\u884C\u8BED\u6CD5\u5206\u6790\uFF0C\u8FDB\u800C\u8FA8\u6790\u6210\u8BED\u8BED\u6CD5\u529F\u80FD\u7684\u5F02\u540C\u3002</p><p>https://zwzt-h5.zuowenzhitiao.com/share/long.html?v=0x25030640&amp;type=2&amp;articleId=TkRjME1R</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2019-05-31/bc38ee53f2214ab989153989dd0f4707.jpg" alt="img" loading="lazy"></p><p>\u6B64\u5916\uFF0C\u9700\u5F52\u7EB3\u8BED\u8A00\u5E94\u7528\u7684\u76F8\u5173\u9898\u578B\u53CA\u7B54\u9898\u8981\u70B9\u3002\u60C5\u666F\u5BF9\u8BDD\u9898\u5F3A\u8C03\u8BED\u8A00\u7684\u5B9E\u9645\u4EA4\u9645\u529F\u80FD\uFF0C\u8981\u6C42\u8FDB\u5165\u60C5\u5883\u53BB\u4F53\u9A8C\u548C\u601D\u8003\uFF0C\u56E0\u800C\u5C0F\u7EB8\u6761\u4EEC\u53EF\u4EE5\u603B\u7ED3\u4E0D\u540C\u5177\u4F53\u4EA4\u9645\u8BED\u7684\u683C\u5F0F\u4EE5\u53CA\u76F8\u5173\u672F\u8BED\u7684\u8FD0\u7528\uFF0C\u5982\u201C\u901A\u77E5\u201D\u201C\u8BF7\u5047\u6761\u201D\u201C\u9881\u5956\u8BCD\u201D\u7B49\u7B49\u3002\u53C8\u6BD4\u5982\uFF0C\u590D\u4E60\u56FE\u6587\u8F6C\u6362\u9898\u65F6\uFF0C\u53EF\u4EE5\u603B\u7ED3\u201C\u5C06\u56FE\u6807\u3001\u6F2B\u753B\u3001\u8868\u683C\u3001\u601D\u7EF4\u5BFC\u56FE\u8F6C\u6362\u6210\u6587\u5B57\u201D\u7B49\u4E0D\u540C\u9898\u76EE\u7684\u7B54\u9898\u7B56\u7565\u3002</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-27/62a4175de787479090d74657997bcfc7.jpg" alt="img" loading="lazy"></p><p><strong>\u4F5C \u6587 \u90E8 \u5206</strong></p><p>\u4F5C\u6587\u53EF\u8C13\u662F\u8BED\u6587\u8BD5\u5377\u7684\u201C\u534A\u58C1\u6C5F\u5C71\u201D\uFF0C\u5728\u6700\u540E\u7684100\u5929\u4E2D\uFF0C\u5EFA\u8BAE\u5C0F\u7EB8\u6761\u4EEC\u628A\u81EA\u5DF1\u4EE5\u524D\u5199\u8FC7\u7684\u4F5C\u6587\u6216\u79EF\u7D2F\u7684\u8303\u6587\uFF0C\u6309\u7167\u5185\u5BB9\u53CA\u6587\u4F53\u8FDB\u884C\u5206\u7C7B\u3002\u6B64\u5916\uFF0C\u8FD8\u79EF\u6781\u5173\u6CE8\u8FD1\u671F\u7684\u793E\u4F1A\u70ED\u70B9\u73B0\u8C61\uFF0C\u529B\u6C42\u8BA9\u6587\u7AE0\u5177\u5907\u65F6\u4EE3\u8272\u5F69\u3002\u5F53\u7136\uFF0C\u7EB8\u6761\u541B\u4E5F\u4F1A\u6301\u7EED\u8F93\u51FA\u7CBE\u70BC\u3001\u6709\u4EE3\u8868\u6027\u7684\u4F5C\u6587\u7D20\u6750\uFF0C\u5E2E\u52A9\u5C0F\u7EB8\u6761\u4EEC\u8FBE\u5230\u201C\u4E00\u65E5\u80DC\u5341\u65E5\u201D\u7684\u5B66\u4E60\u76EE\u6807\uFF01</p><p>\u201C\u8FD0\u7528\u4E4B\u5999\uFF0C\u5B58\u4E4E\u4E00\u5FC3\u201D\uFF0C\u4F73\u4F5C\u96BE\u4EE5\u5076\u6210\uFF0C\u8003\u524D\u4E0D\u95F4\u65AD\u7684\u7EC3\u7B14\u5FC5\u4E0D\u53EF\u5C11\u3002\u7EB8\u6761\u541B\u6BCF\u5468\u90FD\u4F1A\u63A8\u51FA\u7CBE\u9009\u7EC3\u7B14\u9898\u76EE\uFF0C\u5E0C\u671B\u5C0F\u7EB8\u6761\u4EEC\u628A\u63E1\u6BCF\u6B21\u7EC3\u7B14\u673A\u4F1A\uFF0C\u591A\u591A\u5728\u7EC3\u7B14\u533A\u5206\u4EAB\u4F73\u4F5C\uFF0C\u8BF4\u4E0D\u5B9A\u4F1A\u5F97\u5230\u7EB8\u6761\u541B\u6216\u5927\u4F6C\u4EEC\u7684\u7CBE\u5FC3\u70B9\u8BC4\u54E6~</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-27/29b0042e13394fbda996c887f4545ea6.jpg" alt="img" loading="lazy"></p><p>\u90A3\u4E48\uFF0C\u5728\u6700\u540E100\u5929\u7684\u4F5C\u6587\u8BAD\u7EC3\u4E2D\uFF0C\u5C0F\u7EB8\u6761\u4EEC\u8981\u5145\u5206\u5229\u7528\u201C\u9996\u56E0\u6548\u5E94\u201D\uFF0C\u5373\u8BA9\u4E60\u4F5C\u4EA7\u751F\u8F83\u5F3A\u7684\u201C\u89C6\u89C9\u51B2\u51FB\u529B\u201D\uFF0C\u907F\u514D\u201C\u72B9\u62B1\u7435\u7436\u534A\u906E\u9762\u201D\u3002\u6B64\u5916\uFF0C\u8981\u91CD\u70B9\u638C\u63E1\u5728\u5173\u952E\u5904\u6216\u9192\u76EE\u5904\u70B9\u9898\u7684\u65B9\u6CD5\uFF0C\u8B6C\u5982\u6807\u9898\u70B9\u9898\u3001\u5C0F\u6807\u9898\u70B9\u9898\u3001\u9898\u8BB0\u70B9\u9898\u3001\u5F00\u5934\u70B9\u9898\u7B49\u7B49\u3002\u603B\u4E4B\uFF0C\u5C0F\u7EB8\u6761\u4EEC\u5728\u6BCF\u6B21\u7EC3\u7B14\u540E\u8981\u201C\u5341\u7701\u543E\u6587\u201D\uFF0C\u5E76\u9010\u4E00\u653B\u7834\u4E0D\u8DB3\u4E4B\u5904\u3002</p><p><strong>1. \u6807\u9898\u662F\u5426\u591F\u201C\u9753\u201D\u3002</strong>\uFF08\u65E2\u8981\u663E\u793A\u9898\u610F\uFF0C\u53C8\u8981\u751F\u52A8\u65B0\u9896\u3002\uFF09</p><p><strong>2. \u6587\u4F53\u662F\u5426\u591F\u201C\u50CF\u201D\u3002</strong>\uFF08\u638C\u63E1\u4E66\u4FE1\u3001\u6F14\u8BB2\u7A3F\u3001\u5021\u8BAE\u4E66\u7B49\u5404\u79CD\u6587\u4F53\u7684\u5199\u4F5C\u65B9\u6CD5\u3002\uFF09</p><p><strong>3. \u7ACB\u610F\u662F\u5426\u591F\u201C\u51C6\u201D\u3002</strong>\uFF08\u9700\u7B26\u5408\u9898\u610F\uFF0C\u907F\u514D\u8DD1\u9898\u3001\u5957\u4F5C\u3001\u6284\u88AD\u3002\uFF09</p><p><strong>4. \u9009\u6750\u662F\u5426\u591F\u201C\u65B0\u201D\u3002</strong>\uFF08\u89C1\u89E3\u65B0\u9896\uFF0C\u6750\u6599\u65B0\u9C9C\uFF0C\u6784\u601D\u65B0\u5DE7\u3002\uFF09</p><p><strong>5. \u8BED\u8A00\u662F\u5426\u591F\u201C\u7F8E\u201D\u3002</strong>\uFF08\u5177\u6709\u6587\u91C7\u3001\u7075\u6D3B\u91C7\u7528\u591A\u79CD\u53E5\u5F0F\u3002\uFF09</p><p><strong>6. \u9996\u5C3E\u662F\u5426\u591F\u201C\u54CD\u201D\u3002</strong>\uFF08\u7559\u4E0B\u521D\u6B65\u7684\u597D\u5370\u8C61\uFF0C\u5E76\u4F7F\u9605\u5377\u8001\u5E08\u201C\u56DE\u5473\u65E0\u7A77\u201D\u3002\uFF09</p><p><strong>7. \u601D\u8003\u662F\u5426\u591F\u201C\u6DF1\u201D\u3002</strong>\uFF08\u5373\u601D\u60F3\u9700\u5065\u5EB7\uFF0C\u900F\u8FC7\u73B0\u8C61\u6DF1\u5165\u672C\u8D28\uFF0C\u8FDB\u884C\u6DF1\u523B\u601D\u8003\u3002\uFF09</p><p><strong>8. \u611F\u60C5\u662F\u5426\u591F\u201C\u771F\u201D\u3002</strong>\uFF08\u987B\u5F97\u611F\u60C5\u771F\u631A\uFF0C\u529B\u907F\u65E0\u75C5\u547B\u541F\u3002\uFF09</p><p><strong>9. \u5B57\u6570\u662F\u5426\u591F\u201C\u591A\u201D\u3002</strong>\uFF08800\u5B57\u662F\u6700\u4F4E\u8981\u6C42\uFF0C\u8BAD\u7EC3\u65F6\u8981\u63D0\u9AD8\u5199\u5B57\u901F\u5EA6\u3002\uFF09</p><p><strong>10. \u5B57\u8FF9\u662F\u5426\u591F\u201C\u5DE5\u201D\u3002</strong>\uFF08\u5373\u4FBF\u4E0D\u80FD\u505A\u5230\u7AEF\u79C0\u9052\u52B2\uFF0C\u4E5F\u8981\u529B\u4FDD\u4E66\u9762\u5DE5\u6574\uFF01\uFF09</p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-27/43a4f38297ae4a2f89efe6b55bac43e4.jpg" alt="img" loading="lazy"></p><p><strong>\u7EB8 \u6761 \u541B \u7684 \u6084 \u6084 \u8BDD</strong></p><p>\u7ACB\u5FD7 \u201C\u5341\u5E74\u78E8\u4E00\u5251\u201D\u7684\u5C0F\u7EB8\u6761\u4EEC\uFF0C\u9AD8\u8003\u5DF2\u8FDB\u5165\u767E\u65E5\u5012\u8BA1\u65F6\uFF0C\u4F60\u4EEC\u662F\u5426\u5DF2\u7ECF\u4E3A\u81EA\u5DF1\u201C\u91CF\u8EAB\u5B9A\u5236\u201D\u4E86\u76F8\u5E94\u7684\u5B66\u4E60\u89C4\u5212\u4E86\u5462\uFF1F\u53C8\u90FD\u7ACB\u4E0B\u4E86\u4EC0\u4E48flag\u5440\uFF1F\u5728\u5BB6\u8FDB\u884C\u7684\u7F51\u7EDC\u6559\u5B66\u8FD8\u987A\u5229\u5417\uFF1F\u5C0F\u7EB8\u6761\u4EEC\u4E0D\u59A8\u4E5F\u9AD8\u547C\u51FA\u81EA\u5DF1\u7684\u76EE\u6807\u9662\u6821\uFF0C\u7559\u4E0B\u81EA\u5DF1\u767E\u65E5\u8A93\u5E08\u7684\u5BA3\u8A00\uFF0C\u6216\u8BB8\u8FD8\u80FD\u610F\u5916\u6355\u6349\u5230\u7ED9\u81EA\u5DF1\u52A0\u6CB9\u9F13\u52B2\u7684\u672A\u6765\u6821\u53CB\u5462\uFF01\u5DF2\u7ECF\u8FDB\u5165\u8C61\u7259\u5854\u5B66\u4E60\u7684\u5C0F\u53EF\u7231\u4EEC\uFF0C\u53C8\u6709\u4EC0\u4E48\u8840\u6CEA\u6559\u8BAD\u6216\u5B9D\u8D35\u7ECF\u9A8C\u60F3\u5206\u4EAB\u7ED9\u5373\u5C06\u4E0A\u9AD8\u8003\u201C\u6218\u573A\u201D\u7684\u5B66\u5F1F\u5B66\u59B9\u4EEC\u5462\uFF1F\u6B22\u8FCE\u5927\u5BB6\u4E00\u8D77\u5728\u8BC4\u8BBA\u533A\u4EA4\u6D41\u5206\u4EAB\u5440<sub>(\uFF3E\uFF35\uFF3E)\u30CE</sub></p><p><img src="http://zt-cdn.zuowenzhitiao.com/article-cover/2020-02-27/28b18374e34c49c7bf2c28cbc0e35157.png" alt="img" loading="lazy"></p><p>\u56FE\u7247\u6765\u6E90\u4E8E\u7F51\u7EDC\uFF0C\u4FB5\u5220\u3002</p>',55);function c(e,i){return o}var n=t(a,[["render",c]]);export{n as default};
