import{_ as i,f as l}from"./app.d7441975.js";const a={},n=l(`<h1 id="components-interconnects" tabindex="-1"><a class="header-anchor" href="#components-interconnects" aria-hidden="true">#</a> Components &amp; Interconnects</h1><h2 id="\u8BA1\u7B97\u673A\u7EC4\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u8BA1\u7B97\u673A\u7EC4\u4EF6" aria-hidden="true">#</a> \u8BA1\u7B97\u673A\u7EC4\u4EF6</h2><p>\u8BA1\u7B97\u673A\u4E3B\u8981\u662F\u7531\u4EE5\u4E0B\u4E09\u90E8\u5206\u7EC4\u6210</p><ul><li>\u4E2D\u592E\u5904\u7406\u5668\uFF08CPU\uFF09 <ul><li>CPU =\u63A7\u5236\u5355\u5143\uFF08CU\uFF09+\u7B97\u672F\u548C\u903B\u8F91\u5355\u5143\uFF08ALU\uFF09</li><li>\u4E24\u4E2A\u529F\u80FD\uFF1A\u63A7\u5236\u548C\u8BA1\u7B97</li></ul></li><li>IO <ul><li>\u56E0\u4E3A\u6570\u636E\u548C\u6307\u4EE4\u9700\u8981\u8FDB\u5165\u7CFB\u7EDF</li><li>\u5E76\u4E14\u6211\u4EEC\u9700\u8981\u8F93\u51FA\u7ED3\u679C</li></ul></li><li>\u4E3B\u5B58\uFF08Main Memory\uFF09 <ul><li>\u9700\u8981\u4E34\u65F6\u5B58\u50A8\u4EE3\u7801\uFF08\u7A0B\u5E8F\uFF09\u548C\u7ED3\u679C</li></ul></li></ul><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120133231361.png" alt="" loading="lazy"></p><h2 id="\u5BC4\u5B58\u5668-register-\u5B58\u50A8\u4FE1\u606F\u7684\u5730\u65B9" tabindex="-1"><a class="header-anchor" href="#\u5BC4\u5B58\u5668-register-\u5B58\u50A8\u4FE1\u606F\u7684\u5730\u65B9" aria-hidden="true">#</a> \u5BC4\u5B58\u5668\uFF08Register\uFF09\uFF1A\u5B58\u50A8\u4FE1\u606F\u7684\u5730\u65B9</h2><ul><li>\u7A0B\u5E8F\u8BA1\u6570\u5668\uFF08PC\uFF09\uFF1A\u4E5F\u79F0\u4E3A\u6307\u4EE4\u5730\u5740\u5BC4\u5B58\u5668\uFF1B\u5B83\u5B58\u50A8\u5C06\u8981\u6267\u884C\u7684\u4E0B\u4E00\u6761\u6307\u4EE4\u7684\u5185\u5B58\u5730\u5740\uFF1B\u5728\u83B7\u53D6\u6307\u4EE4\u540E\u5B83\u5C06\u589E\u52A0</li><li>\u6307\u4EE4\u5BC4\u5B58\u5668\uFF08IR\uFF09\uFF1A\u4FDD\u5B58\u5F53\u524D\u6B63\u5728\u6267\u884C\u7684\u6307\u4EE4</li><li>\u5185\u5B58\u5730\u5740\u5BC4\u5B58\u5668\uFF08MAR\uFF09\uFF1A\u5B83\u5B58\u50A8\u5C06\u4ECE\u4E2D\u5C06\u6570\u636E\u63D0\u53D6\u5230CPU\u7684\u5185\u5B58\u5730\u5740\u6216\u5C06\u6570\u636E\u53D1\u9001\u5230\u7684\u5185\u5B58\u5730\u5740</li><li>\u5185\u5B58\u7F13\u51B2\u5BC4\u5B58\u5668\uFF08MBR\uFF09\uFF1A\u5B83\u5B58\u50A8\u4E0E\u5373\u65F6\u8BBF\u95EE\u5B58\u50A8\u5668\u4E4B\u95F4\u4F20\u8F93\u7684\u6570\u636E</li></ul><h2 id="\u6307\u4EE4-instruction" tabindex="-1"><a class="header-anchor" href="#\u6307\u4EE4-instruction" aria-hidden="true">#</a> \u6307\u4EE4\uFF08Instruction\uFF09</h2><ul><li>\u6307\u4EE4\u662F\u7531CPU\u5B8C\u6210\u7684\u7B97\u672F\u6216\u903B\u8F91\u8FD0\u7B97 <ul><li>ADD\uFF1A\u5C06\u4E24\u4E2A\u6570\u5B57\u52A0\u5728\u4E00\u8D77</li><li>COMPARE\uFF1A\u6BD4\u8F83\u6570\u5B57</li><li>JUMP\uFF1A\u8DF3\u8F6C\u5230RAM\u5730\u5740</li><li>LOAD\uFF1A\u5C06\u4FE1\u606F\u4ECERAM\u52A0\u8F7D\u5230CPU</li></ul></li><li>\u6307\u4EE4\u96C6\u662F\u8BA1\u7B97\u673A\u652F\u6301\u7684\u6307\u4EE4\u96C6 <ul><li>\u53D6\u51B3\u4E8E\u786C\u4EF6\u2013\u4E0D\u540C\u7684\u4F53\u7CFB\u7ED3\u6784\u5C06\u652F\u6301\u4E0D\u540C\u7684\u6307\u4EE4\u96C6</li></ul></li></ul><h2 id="\u7A0B\u5E8F-program-\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u7A0B\u5E8F-program-\u662F\u4EC0\u4E48" aria-hidden="true">#</a> \u7A0B\u5E8F\uFF08Program\uFF09\u662F\u4EC0\u4E48</h2><p>\u7A0B\u5E8F\uFF1A\u4E00\u7CFB\u5217\u6307\u4EE4</p><ul><li>\u4E0D\u80FD\u4F7F\u7528C\uFF0CJava\uFF0CPython\u7B49\u9AD8\u7EA7\u8BED\u8A00\u7F16\u5199\u7A0B\u5E8F\u8BED\u53E5\uFF0C\u4F46\u53EF\u4EE5\u628A\u4ED6\u4EEC\u7FFB\u8BD1\u6210\u7A0B\u5E8F\u8BED\u53E5</li><li>\u6BCF\u4E2A\u7A0B\u5E8F\u8BED\u53E5\u5C06\u8F6C\u6362\u4E3A\u4E00\u7CFB\u5217\u6307\u4EE4</li><li>\u4F8B\u5982\uFF1AIN\uFF0CADD\uFF0CJUMP\uFF0CLOAD\uFF0COUT</li><li>\u65E8\u5728\u5B9E\u73B0\u4E00\u5B9A\u529F\u80FD</li></ul><p>\u673A\u5668\u8BED\u8A00\u7F16\u5199\u7A0B\u5E8F\u4E0D\u4EC5\u96BE\u8BFB,\u96BE\u5199\u800C\u4E14\u975E\u5E38\u4E0D\u7075\u6D3B.</p><h3 id="\u7528\u6C47\u7F16\u8BED\u8A00\u5F00\u53D1\u7A0B\u5E8F" tabindex="-1"><a class="header-anchor" href="#\u7528\u6C47\u7F16\u8BED\u8A00\u5F00\u53D1\u7A0B\u5E8F" aria-hidden="true">#</a> \u7528\u6C47\u7F16\u8BED\u8A00\u5F00\u53D1\u7A0B\u5E8F</h3><p>\u82E5\u7528\u7B26\u53F7\u8868\u793A\u8DF3\u8F6C\u4F4D\u7F6E\u548C\u53D8\u91CF\u4F4D\u7F6E\uFF0C\u662F\u5426\u7B80\u5316\u4E86\u95EE\u9898\uFF1F</p><p>\u4E8E\u662F\uFF0C\u6C47\u7F16\u8BED\u8A00\u51FA\u73B0\uFF1A\u7528\u52A9\u8BB0\u7B26\u8868\u793A\u64CD\u4F5C\u7801\uFF0C\u7528\u6807\u53F7\u8868\u793A\u4F4D\u7F6E\uFF0C\u7528\u52A9\u8BB0\u7B26\u8868\u793A\u5BC4\u5B58\u5668</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120145327752.png" alt="" loading="lazy"></p><p>\u7528\u6C47\u7F16\u8BED\u8A00\u7F16\u5199\u7684\u4F18\u70B9\u662F\uFF1A \u4E0D\u4F1A\u56E0\u4E3A\u589E\u51CF\u6307\u4EE4\u800C\u9700\u8981\u4FEE\u6539\u5176\u4ED6\u6307\u4EE4 \u4E0D\u9700\u8BB0\u5FC6\u6307\u4EE4\u7F16\u7801\uFF0C\u7F16\u5199\u65B9\u4FBF \u53EF\u8BFB\u6027\u6BD4\u673A\u5668\u8BED\u8A00\u5F3A\u3002\u4E0D\u8FC7\uFF0C\u8FD9\u5E26\u6765\u65B0\u7684\u95EE\u9898\uFF0C\u662F\u4EC0\u4E48\u5462\uFF1F\u4EBA\u5BB9\u6613\u4E86\uFF0C\u53EF\u673A\u5668\u4E0D\u8BA4\u8BC6\u8FD9\u4E9B\u6307\u4EE4\u4E86.\u6240\u4EE5\uFF0C\u6211\u4EEC\u9700\u5C06\u6C47\u7F16\u8BED\u8A00\u8F6C \u6362\u4E3A\u673A\u5668\u8BED\u8A00\uFF01 \u6211\u4EEC\u7528\u6C47\u7F16\u7A0B\u5E8F\u4F5C\u4E3A\u4E2D\u4ECB\u8FDB\u884C\u8F6C\u6362</p><p>\u4F46\u662F\uFF0C\u6211\u4EEC\u8FDB\u4E00\u6B65\u8BA4\u8BC6\u6C47\u7F16\u8BED\u8A00\uFF0C\u4F1A\u53D1\u73B0\u6C47\u7F16\u8BED\u8A00(\u6E90)\u7A0B\u5E8F\u7531\u6C47\u7F16\u6307\u4EE4\u6784\u6210</p><ul><li>\u4EC0\u4E48\u662F\u6C47\u7F16\u6307\u4EE4\uFF1F <ul><li>\u7528\u52A9\u8BB0\u7B26\u548C\u6807\u53F7\u6765\u8868\u793A\u7684\u6307\u4EE4\uFF08\u4E0E\u673A\u5668\u6307\u4EE4\u4E00\u4E00\u5BF9\u5E94\uFF09</li></ul></li><li>\u6307\u4EE4\u53C8\u662F\u4EC0\u4E48\u5462\uFF1F <ul><li>\u5305\u542B\u64CD\u4F5C\u7801\u548C\u64CD\u4F5C\u6570\u6216\u5176\u5730\u5740\u7801 <ul><li>\u673A\u5668\u6307\u4EE4\u7528\u4E8C\u8FDB\u5236\u8868\u793A</li><li>\u6C47\u7F16\u6307\u4EE4\u7528\u7B26\u53F7\u8868\u793A</li></ul></li><li>\u53EA\u80FD\u8868\u8FBE\u8FD9\u4E9B\uFF1A\u53D6\uFF08\u6216\u5B58\u4E00\u4E2A\u6570\uFF09\u3001\u4E24\u4E2A\u6570\u52A0\uFF08\u6216\u51CF\u3001\u4E58\u3001\u9664\u3001\u4E0E\u3001\u6216\u7B49\uFF09\u3001\u6839\u636E\u8FD0\u7B97\u7ED3\u679C\u5224\u65AD\u662F\u5426\u8F6C\u79FB\u6267\u884C</li></ul></li></ul><p>\u60F3\u8C61\u7528\u6C47\u7F16\u8BED\u8A00\u7F16\u5199\u590D\u6742\u7A0B\u5E8F\u662F\u600E\u6837\u7684\u60C5\u5F62\uFF1F \uFF08\u4F8B\u5982\uFF0C\u7528\u6C47\u7F16\u8BED\u8A00\u5B9E\u73B0\u6392\u5E8F\uFF08sort\uFF09\u3001\u77E9\u9635\u76F8\u4E58\uFF09 \u9700\u8981\u63CF\u8FF0\u7684\u7EC6\u8282\u592A\u591A\u4E86\uFF01\u7A0B\u5E8F\u4F1A\u5F88\u957F\u5F88\u957F\uFF01\u800C\u4E14\u5728\u4E0D\u540C \u7ED3\u6784\u7684\u673A\u5668\u4E0A\u5C31\u4E0D\u80FD\u8FD0\u884C\uFF01</p><p>\u673A\u5668\u8BED\u8A00\u548C\u6C47\u7F16 \u8BED\u8A00\u90FD\u662F\u9762\u5411\u673A \u5668\u7ED3\u6784\u7684\u8BED\u8A00\uFF0C \u6545\u5B83\u4EEC\u7EDF\u79F0\u4E3A\u673A \u5668\u7EA7\u8BED\u8A00</p><p>\u7ED3\u8BBA\uFF1A\u7528\u6C47\u7F16\u8BED\u8A00\u6BD4\u673A\u5668\u8BED\u8A00\u597D\uFF0C\u4F46\u662F\uFF0C\u8FD8\u662F\u5F88\u9EBB\u70E6\uFF01</p><h3 id="\u9AD8\u7EA7\u7F16\u7A0B\u8BED\u8A00" tabindex="-1"><a class="header-anchor" href="#\u9AD8\u7EA7\u7F16\u7A0B\u8BED\u8A00" aria-hidden="true">#</a> \u9AD8\u7EA7\u7F16\u7A0B\u8BED\u8A00</h3><ul><li>\u5B83\u4EEC\u4E0E\u5177\u4F53\u673A\u5668\u7ED3\u6784\u65E0\u5173</li><li>\u9762\u5411\u7B97\u6CD5\u63CF\u8FF0\uFF0C\u6BD4\u673A\u5668\u7EA7\u8BED\u8A00\u63CF\u8FF0\u80FD\u529B\u5F3A\u5F97\u591A</li><li>\u9AD8\u7EA7\u8BED\u8A00\u4E2D\u4E00\u6761\u8BED\u53E5\u5BF9\u5E94\u51E0\u6761\u3001\u51E0\u5341\u6761\u751A\u81F3\u51E0\u767E\u6761\u6307\u4EE4</li><li>\u6709\u201C\u9762\u5411\u8FC7\u7A0B\u201D\u548C\u201C\u9762\u5411\u5BF9\u8C61\u201D\u7684\u8BED\u8A00\u4E4B\u5206</li><li>\u5904\u7406\u903B\u8F91\u5206\u4E3A\u4E09\u79CD\u7ED3\u6784 \u2022 \u987A\u5E8F\u7ED3\u6784\u3001\u9009\u62E9\u7ED3\u6784\u3001\u5FAA\u73AF\u7ED3\u6784</li><li>\u6709\u4E24\u79CD\u8F6C\u6362\u65B9\u5F0F\uFF1A\u201C\u7F16\u8BD1\u201D\u548C\u201C\u89E3\u91CA\u201D <ul><li>\u7F16\u8BD1\u7A0B\u5E8F(Complier)\uFF1A\u5C06\u9AD8\u7EA7\u8BED\u8A00\u6E90\u7A0B\u5E8F\u8F6C\u6362\u4E3A\u673A\u5668\u7EA7\u76EE \u6807\u7A0B\u5E8F\uFF0C\u6267\u884C\u65F6\u53EA\u8981\u542F\u52A8\u76EE\u6807\u7A0B\u5E8F\u5373\u53EF</li><li>\u89E3\u91CA\u7A0B\u5E8F(Interpreter )\uFF1A\u5C06\u9AD8\u7EA7\u8BED\u8A00\u8BED\u53E5\u9010\u6761\u7FFB\u8BD1\u6210\u673A\u5668 \u6307\u4EE4\u5E76\u7ACB\u5373\u6267\u884C\uFF0C\u4E0D\u751F\u6210\u76EE\u6807\u6587\u4EF6</li></ul></li></ul><h3 id="\u7ECF\u5178\u6848\u4F8B-hello-world\u7684\u7F16\u5199" tabindex="-1"><a class="header-anchor" href="#\u7ECF\u5178\u6848\u4F8B-hello-world\u7684\u7F16\u5199" aria-hidden="true">#</a> \u7ECF\u5178\u6848\u4F8B\uFF1AHello\uFF0Cworld\u7684\u7F16\u5199</h3><p>\u6211\u4EEC\u7528C++ \u4E00\u4E2A\u7B80\u5355\u7684\u7A0B\u5E8F</p><div class="language-c ext-c line-numbers-mode"><pre class="language-c"><code><span class="token macro property"><span class="token directive-hash">#</span><span class="token directive keyword">include</span> <span class="token string">&lt;stdio.h&gt;</span></span>
<span class="token keyword">int</span> <span class="token function">main</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
<span class="token punctuation">{</span>
<span class="token function">printf</span><span class="token punctuation">(</span>hello<span class="token punctuation">,</span> world\\n<span class="token punctuation">)</span><span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br></div></div><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120150327166.png" alt="" loading="lazy"><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120150406829.png" alt="" loading="lazy"></p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120150426458.png" alt="" loading="lazy"></p><p>\u6570\u636E\u7ECF\u5E38\u5728\u5404\u5B58\u50A8\u90E8\u4EF6\u95F4\u4F20\u9001\u3002\u6545\u73B0\u4EE3\u8BA1\u7B97\u673A\u5927\u591A\u91C7\u7528\u201C\u7F13\u5B58\u201D\u6280\u672F<br> \u6240\u6709\u8FC7\u7A0B\u90FD\u662F\u5728CPU\u6267\u884C\u6307\u4EE4\u6240\u4EA7\u751F\u7684\u63A7\u5236\u4FE1\u53F7\u7684\u4F5C\u7528\u4E0B\u8FDB\u884C\u7684</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120150640628.png" alt="" loading="lazy"></p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210122050908628.png" alt="" loading="lazy"></p><h2 id="\u6307\u4EE4\u5468\u671F-instruction-cycle" tabindex="-1"><a class="header-anchor" href="#\u6307\u4EE4\u5468\u671F-instruction-cycle" aria-hidden="true">#</a> \u6307\u4EE4\u5468\u671F\uFF08Instruction Cycle\uFF09</h2><p>\u4E24\u4E2A\u4E3B\u8981\u6B65\u9AA4\uFF1A\u53D6\u3001\u6267\u884C <strong>\u6307\u4EE4\u5468\u671F\uFF08Instruction Cycle\uFF09 = \u63D0\u53D6\u5468\u671F\uFF08Fetch Cycle\uFF09+ \u6267\u884C\u5468\u671F\uFF08Execute Cycle\uFF09</strong>, \u53EF\u80FD\u4F1A\u6709\u4E2D\u4E2D\u65AD\u5468\u671F</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120134928829.png" alt="" loading="lazy"></p><p>\u6240\u6709\u6B65\u9AA4\u5747\u7531\u63A7\u5236\u5355\u5143\u4F7F\u7528\u5185\u7F6E\u65F6\u949F\uFF08\u5468\u671F\uFF09\u63A7\u5236\uFF1Asynchronization \u56E0\u6B64\uFF0C\u4E00\u6761\u6307\u4EE4\u901A\u5E38\u6D88\u8017\u4E00\u4E2A\u4EE5\u4E0A\u7684\u65F6\u949F\u5468\u671F\uFF08\u6B65\u9AA4\u548C\u987A\u5E8F\u7684\u8C03\u8282\u5668\uFF09</p><h2 id="\u63D0\u53D6\u5468\u671F-fetch-cycle-\u4E2D\u7684\u52A8\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u63D0\u53D6\u5468\u671F-fetch-cycle-\u4E2D\u7684\u52A8\u4F5C" aria-hidden="true">#</a> \u63D0\u53D6\u5468\u671F\uFF08Fetch Cycle\uFF09\u4E2D\u7684\u52A8\u4F5C</h2><ul><li>\u7A0B\u5E8F\u8BA1\u6570\u5668\uFF08PC\uFF09\u5DF2\u4FDD\u5B58\u8981\u63D0\u53D6\u7684\u4E0B\u4E00\u6761\u6307\u4EE4\u7684\u5730\u5740 <ul><li>\u6307\u4EE4\u987A\u5E8F\u6267\u884C</li></ul></li><li>\u5904\u7406\u5668\u4ECEPC\u6307\u5411\u7684\u5185\u5B58\u4F4D\u7F6E\u83B7\u53D6\u6307\u4EE4\uFF08\u9700\u8981\u5148\u5C06\u5730\u5740\u653E\u5165MAR\uFF09</li><li>\u6307\u4EE4\u52A0\u8F7D\u5230\u6307\u4EE4\u5BC4\u5B58\u5668\uFF08IR\uFF09\u4E2D\uFF08\u9700\u8981\u5148\u5C06\u6307\u4EE4\u653E\u5165MBR\uFF09</li><li>PC\u8BA1\u6570\u5668\u589E\u91CF\uFF08\u987A\u5E8F\u6267\u884C\uFF09 <ul><li>\u9664\u975E\u5728\u6267\u884C\u5468\u671F\u4E2D\u53E6\u6709\u8BF4\u660E\uFF08\u4F8B\u5982\uFF0C\u4ECEIF-THEN-ELSE\uFF0C\u5FAA\u73AF\u8BED\u53E5\uFF09</li></ul></li><li>\u5904\u7406\u5668\u89E3\u91CA\u6307\u4EE4\u5E76\u51C6\u5907\u6267\u884C\u6240\u9700\u7684\u64CD\u4F5C</li></ul><h2 id="\u6267\u884C\u5468\u671F-execute-cycle-\u4E2D\u7684\u52A8\u4F5C" tabindex="-1"><a class="header-anchor" href="#\u6267\u884C\u5468\u671F-execute-cycle-\u4E2D\u7684\u52A8\u4F5C" aria-hidden="true">#</a> \u6267\u884C\u5468\u671F\uFF08Execute Cycle\uFF09\u4E2D\u7684\u52A8\u4F5C</h2><p>\u53D6\u51B3\u4E8E\u6307\u4EE4</p><ul><li>\u5904\u7406\u5668-\u5185\u5B58 <ul><li>CPU\u548C\u4E3B\u5B58\u50A8\u5668\u4E4B\u95F4\u7684\u6570\u636E\u4F20\u8F93</li></ul></li><li>\u5904\u7406\u5668-I / O <ul><li>CPU\u548CI / O\u6A21\u5757\u4E4B\u95F4\u7684\u6570\u636E\u4F20\u8F93</li></ul></li><li>\u6570\u636E\u5904\u7406 <ul><li>\u5BF9\u6570\u636E\u8FDB\u884C\u4E00\u4E9B\u7B97\u672F\u6216\u903B\u8F91\u8FD0\u7B97\uFF08ALU\u8FD0\u7B97\uFF09</li></ul></li><li>\u63A7\u5236 <ul><li>\u64CD\u4F5C\u987A\u5E8F\u7684\u53D8\u66F4\uFF0C\u4F8B\u5982\u5206\u652F\u548C\u8DF3\u8F6C\uFF08\u6765\u81EA\u5FAA\u73AF\uFF0C\u51FD\u6570\u8C03\u7528\u7B49\u8BED\u53E5\uFF09</li></ul></li><li>\u4EE5\u4E0A\u7EC4\u5408</li></ul><h2 id="\u7A0B\u5E8F\u6267\u884C\u793A\u4F8B" tabindex="-1"><a class="header-anchor" href="#\u7A0B\u5E8F\u6267\u884C\u793A\u4F8B" aria-hidden="true">#</a> \u7A0B\u5E8F\u6267\u884C\u793A\u4F8B</h2><p>\u6700\u521D\uFF1APC\uFF1A300\u6307\u4EE4\u662FIR\u4E2D\u7684\u7B2C\u4E00\u4F4D</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120140002851.png" alt="" loading="lazy"></p><ol><li><p>\u9996\u5148\uFF0C\u7A0B\u5E8F\u8BA1\u6570\u5668\uFF08\u4E5F\u79F0\u4E3A\u6307\u4EE4\u5730\u5740\u5BC4\u5B58\u5668/PC\uFF09\u4E2D\u8BB0\u5F55\u4E86\u5185\u5B58\u4E2D\u4E0B\u4E00\u6761\u5E94\u8BE5\u6267\u884C\u7684\u6307\u4EE4\u5730\u5740\u4E3A300</p></li><li><p>\u4E4B\u540E\uFF0CIR\u83B7\u53D6\u4E86\u5730\u5740\u4E3A\u5728\u5185\u5B58\u4E2D\u5730\u5740\u4E3A300\u7684\u7A0B\u5E8F\u6307\u4EE41940\uFF08\u867D\u7136\u6211\u4EEC\u4E0D\u77E5\u90531940\u4EE3\u8868\u7684\u6307\u4EE4\u662F\u4EC0\u4E48\u610F\u601D,\u6211\u4EEC\u5047\u8BBE\u5B83\u4EE3\u8868\u7684\u610F\u601D\u4E3A\u83B7\u53D6\u5185\u5B58\u5730\u5740\u4E3A940\u7684\u503C\uFF0C\u6545AC\u6B64\u65F6\u7F16\u7A0B0003\uFF09</p></li><li><p>\u540C\u65F6\uFF0CPC\u66F4\u65B0\u4E3A\u4E0B\u4E00\u4E2A\u5185\u5B58\u5730\u5740301\u3002</p></li><li><p>\u4E4B\u540E\uFF0CIR\u83B7\u53D6\u4E86\u5730\u5740\u4E3A\u5728\u5185\u5B58\u4E2D\u5730\u5740\u4E3A301\u7684\u7A0B\u5E8F\u6307\u4EE45941\uFF0C\uFF08\u867D\u7136\u6211\u4EEC\u4E0D\u77E5\u90535941\u4EE3\u8868\u7684\u6307\u4EE4\u662F\u4EC0\u4E48\u610F\u601D,\u6211\u4EEC\u5047\u8BBE\u5B83\u4EE3\u8868\u7684\u610F\u601D\u4E3A\u628A\u5185\u5B58\u5730\u5740\u4E3A941\u7684\u503C\u548C\u5F53\u524D\u7684\u503C\u8FDB\u884C\u52A0\u548C\u5E76\u5B58\u50A8\u5230AC\u4E2D\uFF0C\u6545AC\u6B64\u65F6\u7F16\u7A0B0005\uFF09</p></li><li><p>\u540C\u65F6\uFF0CPC\u66F4\u65B0\u4E3A\u4E0B\u4E00\u4E2A\u5185\u5B58\u5730\u5740302.</p></li><li><p>\u4E4B\u540E\uFF0CIR\u83B7\u53D6\u4E86\u5730\u5740\u4E3A\u5728\u5185\u5B58\u4E2D\u5730\u5740\u4E3A302\u7684\u7A0B\u5E8F\u6307\u4EE42941\uFF08\u867D\u7136\u6211\u4EEC\u4E0D\u77E5\u90532941\u4EE3\u8868\u7684\u6307\u4EE4\u662F\u4EC0\u4E48\u610F\u601D,\u6211\u4EEC\u5047\u8BBE\u5B83\u4EE3\u8868\u7684\u610F\u601D\u4E3A\u628A\u5F53\u524DAC\u7684\u503C\u5B58\u50A8\u5230\u5185\u5B58\u5730\u5740\u4E3A941\u7684\u5730\u65B9\uFF0C\u6545AC\u6B64\u65F6\u7F16\u7A0B0003\uFF09</p></li><li><p>\u540C\u65F6\uFF0CPC\u66F4\u65B0\u4E3A\u4E0B\u4E00\u4E2A\u5185\u5B58\u5730\u5740303.</p></li><li><p>......</p></li></ol><p>\u7528\u56FE\u6765\u770B\u662F\u8FD9\u6837\u7684</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120142135741.png" alt="" loading="lazy"></p><h2 id="\u4E2D\u65AD-interrupts" tabindex="-1"><a class="header-anchor" href="#\u4E2D\u65AD-interrupts" aria-hidden="true">#</a> \u4E2D\u65AD\uFF08Interrupts\uFF09</h2><p>\u4E2D\u65AD\uFF1A\u5176\u4ED6\u6A21\u5757\uFF08\u4F8B\u5982I / O\uFF09\u53EF\u4EE5\u4E2D\u65AD\u6B63\u5E38\u5904\u7406\u987A\u5E8F\u7684\u673A\u5236 \u95EE\u9898\uFF1A\u60F3\u8C61\u4E00\u4E0B\uFF0C\u5982\u679C\u4F60\u5728\u901A\u8BDD\u4E2D\u5E76\u63A5\u5230\u53E6\u4E00\u4E2A\u6765\u7535\uFF0C\u8BE5\u600E\u4E48\u529E\uFF1F</p><p>\u4E5F\u8BB8\u6211\u4F1A\u8003\u8651\u8FD9\u53E6\u4E00\u4E2A\u6765\u7535\u662F\u5426\u91CD\u8981\uFF0C\u91CD\u8981\u7684\u8BDD\uFF0C\u6211\u4F1A\u63A5\u542C\u3002\u4E0D\u91CD\u8981\u7684\u8BDD\uFF0C\u6211\u4F1A\u7B49\u5F53\u524D\u7684\u7535\u8BDD\u6253\u5B8C\u540E\u518D\u5904\u7406\u53E6\u4E00\u4E2A\uFF0C\u5373\u5224\u65AD\u662F\u5426\u6709\u63A5\u542C\u7684\u5FC5\u8981</p><p>\u4E2D\u65AD\u6E90</p><ul><li>\u7A0B\u5E8F\uFF0C\u4F8B\u5982\u6EA2\u51FA\uFF0C\u9664\u4EE5\u96F6</li><li>\u8BA1\u65F6\u5668\uFF0C\u4F8B\u5982\u7531\u5185\u90E8\u5904\u7406\u5668\u8BA1\u65F6\u5668\u751F\u6210</li><li>I / O\uFF0C\u4F8B\u5982\u4ECEI / O\u63A7\u5236\u5668</li><li>\u786C\u4EF6\u6545\u969C\uFF0C\u4F8B\u5982\u5185\u5B58\u5947\u5076\u6821\u9A8C\u9519\u8BEF</li></ul><h2 id="\u901A\u8FC7\u4E2D\u65AD\u8F6C\u79FB\u63A7\u5236-transfer-of-control-via-interrupts" tabindex="-1"><a class="header-anchor" href="#\u901A\u8FC7\u4E2D\u65AD\u8F6C\u79FB\u63A7\u5236-transfer-of-control-via-interrupts" aria-hidden="true">#</a> \u901A\u8FC7\u4E2D\u65AD\u8F6C\u79FB\u63A7\u5236\uFF08Transfer of Control via Interrupts\uFF09</h2><p>\u5982\u679C\u53D1\u751F\u4E2D\u65AD\uFF1A</p><ol><li>\u5904\u7406\u5668\u6682\u505C\u5F53\u524D\u7A0B\u5E8F\u7684\u6267\u884C\u5E76\u4FDD\u5B58\u5176\u4E0A\u4E0B\u6587</li><li>\u5C06PC\u8BBE\u7F6E\u4E3A\u4E2D\u65AD\u5904\u7406\u7A0B\u5E8F\u7684\u8D77\u59CB\u5730\u5740</li></ol><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120142819666.png" alt="" loading="lazy"></p><h2 id="\u4E2D\u65AD\u5468\u671F-interrupt-cycle" tabindex="-1"><a class="header-anchor" href="#\u4E2D\u65AD\u5468\u671F-interrupt-cycle" aria-hidden="true">#</a> \u4E2D\u65AD\u5468\u671F\uFF08Interrupt Cycle\uFF09</h2><p>\u4E2D\u65AD\u53EF\u80FD\u4E0D\u53EF\u907F\u514D\uFF0C\u6240\u4EE5\uFF0C\u6211\u4EEC\u9700\u8981\u5236\u5B9A\u76F8\u5173\u6D41\u7A0B</p><ol><li>\u5904\u7406\u5668\u68C0\u67E5\u4E2D\u65AD</li><li>\u7531\u4E2D\u65AD\u4FE1\u53F7\u6307\u793A</li><li>\u5982\u679C\u6CA1\u6709\u4E2D\u65AD\uFF0C\u5219\u83B7\u53D6\u4E0B\u4E00\u6761\u6307\u4EE4</li><li>\u5982\u679C\u4E2D\u65AD\u5F85\u5904\u7406\uFF1A</li><li>\u6682\u505C\u5F53\u524D\u7A0B\u5E8F\u7684\u6267\u884C</li><li>\u4FDD\u5B58\u4E0A\u4E0B\u6587</li><li>\u5C06PC\u8BBE\u7F6E\u4E3A\u4E2D\u65AD\u5904\u7406\u7A0B\u5E8F\u4F8B\u7A0B\u7684\u8D77\u59CB\u5730\u5740</li><li>\u5904\u7406\u4E2D\u65AD</li><li>\u6062\u590D\u4E0A\u4E0B\u6587\u5E76\u7EE7\u7EED\u4E2D\u65AD\u7684\u7A0B\u5E8F</li></ol><h2 id="\u5E26\u4E2D\u65AD\u7684\u6307\u4EE4\u5468\u671F-instruction-cycle-with-interrupts" tabindex="-1"><a class="header-anchor" href="#\u5E26\u4E2D\u65AD\u7684\u6307\u4EE4\u5468\u671F-instruction-cycle-with-interrupts" aria-hidden="true">#</a> \u5E26\u4E2D\u65AD\u7684\u6307\u4EE4\u5468\u671F\uFF08Instruction Cycle with Interrupts\uFF09</h2><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120143145203.png" alt="" loading="lazy"></p><p>\u56FE\u89E3</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120143159965.png" alt="" loading="lazy"></p><p>\u7A0B\u5E8F\u6D41\u7A0B\u63A7\u5236\uFF08Program Flow Control\uFF09</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120143402104.png" alt="" loading="lazy"></p><h2 id="\u591A\u6B21\u4E2D\u65AD-multiple-interrupts" tabindex="-1"><a class="header-anchor" href="#\u591A\u6B21\u4E2D\u65AD-multiple-interrupts" aria-hidden="true">#</a> \u591A\u6B21\u4E2D\u65AD\uFF08Multiple Interrupts\uFF09</h2><p>\u65E0\u9700\u4E86\u89E3\u8BE6\u7EC6\u4FE1\u606F\uFF0C\u4EC5\u4F9B\u53C2\u8003</p><p>\u591A\u4E2A\u4E2D\u65AD\uFF1A\u5F53\u524D\u6B63\u5728\u5904\u7406\u4E2D\u65AD\u65F6\u51FA\u73B0\u4E2D\u65AD</p><ul><li>\u89E3\u51B3\u65B9\u68481\uFF1A\u987A\u5E8F\u5904\u7406\uFF1A\u4E2D\u65AD\u6309\u987A\u5E8F\u53D1\u751F <ul><li>\u5904\u7406\u5668\u5728\u5904\u7406\u4E00\u4E2A\u4E2D\u65AD\u65F6\u5C06\u5FFD\u7565\u5176\u4ED6\u4E2D\u65AD</li><li>\u4E2D\u65AD\u4FDD\u6301\u6302\u8D77\u72B6\u6001\uFF0C\u5E76\u5728\u5904\u7406\u5B8C\u7B2C\u4E00\u4E2A\u4E2D\u65AD\u540E\u8FDB\u884C\u68C0\u67E5</li></ul></li></ul><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120143608529.png" alt="" loading="lazy"></p><p>\u89E3\u51B3\u65B9\u68482\uFF1A\u5D4C\u5957\u5904\u7406\uFF1A\u5141\u8BB8\u4E2D\u65AD\u671F\u95F4\u7684\u4E2D\u65AD</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120143632653.png" alt="" loading="lazy"></p><p>\u95EE\u9898\uFF1A\u4E24\u79CD\u65B9\u6CD5\u6709\u4EC0\u4E48\u533A\u522B\uFF1F</p><ul><li>\u5982\u4F55\u5904\u7406\uFF1F \u8BBE\u7F6E\u516C\u6B63\u548C\u7D27\u6025\u7684\u4F18\u5148\u4E8B\u9879</li><li>\u4F4E\u4F18\u5148\u7EA7\u4E2D\u65AD\u53EF\u4EE5\u88AB\u9AD8\u4F18\u5148\u7EA7\u4E2D\u65AD\u6253\u65AD</li><li>\u5904\u7406\u8F83\u9AD8\u4F18\u5148\u7EA7\u7684\u4E2D\u65AD\u540E\uFF0C\u5904\u7406\u5668\u5C06\u8FD4\u56DE\u4E0A\u4E00\u4E2A\u4E2D\u65AD</li></ul><h2 id="\u8FDE\u63A5\u8BA1\u7B97\u673A\u7EC4\u4EF6-\u4E92\u8FDE" tabindex="-1"><a class="header-anchor" href="#\u8FDE\u63A5\u8BA1\u7B97\u673A\u7EC4\u4EF6-\u4E92\u8FDE" aria-hidden="true">#</a> \u8FDE\u63A5\u8BA1\u7B97\u673A\u7EC4\u4EF6\uFF08\u4E92\u8FDE\uFF09</h2><p>\u6240\u6709\u5355\u5143\u5FC5\u987B\u8FDE\u63A5</p><p>\u4E0D\u540C\u7C7B\u578B\u7684\u5355\u5143\u6709\u4E0D\u540C\u7C7B\u578B\u7684\u8FDE\u63A5</p><ul><li>Memory</li><li>Input/Output</li><li>CPU</li></ul><h2 id="\u7535\u8111\u6A21\u5757-\u7EC4\u4EF6" tabindex="-1"><a class="header-anchor" href="#\u7535\u8111\u6A21\u5757-\u7EC4\u4EF6" aria-hidden="true">#</a> \u7535\u8111\u6A21\u5757/\u7EC4\u4EF6</h2><p>\u6240\u6709\u4E09\u4E2A\u7EC4\u4EF6\u5747\u901A\u8FC7bus(es)\u8FDB\u884C\u901A\u4FE1,\u5982\u4E0B\u56FE</p><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120144002302.png" alt="" loading="lazy">## Bus</p><h2 id="interconnection-scheme-\u4E86\u89E3\u5373\u53EF" tabindex="-1"><a class="header-anchor" href="#interconnection-scheme-\u4E86\u89E3\u5373\u53EF" aria-hidden="true">#</a> Interconnection Scheme \uFF08\u4E86\u89E3\u5373\u53EF\uFF09</h2><p><img src="https://hssvr01.oss-cn-zhangjiakou.aliyuncs.com/hongshu/file/2021/01/20210120144147303.png" alt="" loading="lazy"></p><h2 id="bus-\u603B\u7EBF-\u662F\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#bus-\u603B\u7EBF-\u662F\u4EC0\u4E48" aria-hidden="true">#</a> BUS(\u603B\u7EBF) \u662F\u4EC0\u4E48</h2><ul><li><p>\u8FDE\u63A5\u4E24\u4E2A\u6216\u66F4\u591A\u8BBE\u5907\u7684\u901A\u4FE1\u8DEF\u5F84</p><ul><li>\u901A\u5E38\u5E7F\u64AD\uFF0C\u7ECF\u5E38\u5206\u7EC4 <ul><li>\u4E00\u6761\u603B\u7EBF\u4E2D\u7684\u591A\u4E2A\u901A\u9053\uFF0C\u4F8B\u598232\u4F4D\u6570\u636E\u603B\u7EBF\u662F32\u4E2A\u72EC\u7ACB\u7684\u5355\u4E2A\u4F4D\u901A\u9053</li></ul></li></ul></li><li><p>\u4E09\u79CD\u603B\u7EBF\u7C7B\u522B\uFF1A\u6570\u636E\u603B\u7EBF\uFF0C\u5730\u5740\u603B\u7EBF\u548C\u63A7\u5236\u603B\u7EBF</p></li><li><p>\u5355\u603B\u7EBF\u4E0E\u591A\u603B\u7EBF\u7ED3\u6784</p><ul><li>\u4F8B\u5982Unibus\uFF08DEC-PDP\uFF09</li><li>\u4F8B\u5982\u63A7\u5236/\u5730\u5740/\u6570\u636E\u603B\u7EBF\uFF08PC\uFF09</li></ul></li><li><p>\u4E13\u7528\u603B\u7EBF</p><ul><li>\u5206\u914D\u7ED9\u5355\u4E2A\u529F\u80FD\uFF0C\u4F8B\u5982\u7528\u4E8E\u5730\u5740\u6216\u6570\u636E\u7684\u529F\u80FD\uFF0C\u79F0\u4E3A\u5730\u5740\u603B\u7EBF\u548C\u6570\u636E\u603B\u7EBF</li><li>\u4E0D\u540C\u7EC4\u4EF6\u7684\u5206\u79BB\u603B\u7EBF</li></ul></li><li><p>\u591A\u8DEF\u603B\u7EBF</p><ul><li>\u7528\u4E8E\u5730\u5740\u548C\u6570\u636E-\u5171\u4EAB\u7EBF</li><li>\u4F18\u52BF\uFF1A\u884C\u6570\u66F4\u5C11</li><li>\u7F3A\u70B9\uFF1A\u66F4\u590D\u6742\u7684\u63A7\u5236\u548C\u8D44\u6E90\u7684\u987A\u5E8F\u4F7F\u7528\u5C06\u9650\u5236\u6027\u80FD</li></ul></li></ul><h2 id="\u6709\u5173\u5185\u5BB9\u63A8\u8350" tabindex="-1"><a class="header-anchor" href="#\u6709\u5173\u5185\u5BB9\u63A8\u8350" aria-hidden="true">#</a> \u6709\u5173\u5185\u5BB9\u63A8\u8350</h2><ul><li>https://www.doc.ic.ac.uk/~eedwards/compsys/index.html</li></ul><h2 id="\u5F15\u7528\u6750\u6599" tabindex="-1"><a class="header-anchor" href="#\u5F15\u7528\u6750\u6599" aria-hidden="true">#</a> \u5F15\u7528\u6750\u6599</h2><ul><li>William Stallings, \u201CComputer organization and architecture: Designing for Performance\u201D, 8th Edition, 2010</li><li>Dr. Kai Zhou&#39;s PowerPoint</li><li>https://www.cnblogs.com/chenxuming/p/9609463.html</li></ul>`,90);function s(e,r){return n}var h=i(a,[["render",s]]);export{h as default};
