import{_ as p,f as o}from"./app.cb06bc71.js";const t={},a=o('<h1 id="\u6700\u300C\u7B80\u5355\u7684\u5E78\u798F\u300D\u770B\u5B8C\u8C01\u8FD8\u613F\u610F-\u957F\u5927" tabindex="-1"><a class="header-anchor" href="#\u6700\u300C\u7B80\u5355\u7684\u5E78\u798F\u300D\u770B\u5B8C\u8C01\u8FD8\u613F\u610F-\u957F\u5927" aria-hidden="true">#</a> \u6700\u300C\u7B80\u5355\u7684\u5E78\u798F\u300D\u770B\u5B8C\u8C01\u8FD8\u613F\u610F \u957F\u5927</h1><p><audio controls src="https://www.hanjiaming.com.cn/wp-content/uploads/2020/08/20200810170407640.mp3" autoplay loop></audio></p><p>\u6709\u53E5\u8BDD\u8FD9\u4E48\u8BF4\uFF1A\u4E0A\u5B66\u65F6\uFF0C\u5E78\u798F\u662F\u4EF6\u7B80\u5355\u7684\u4E8B\uFF1B\u957F\u5927\u4E86\uFF0C\u7B80\u5355\u5374\u6210\u4E86\u4E00\u79CD\u5E78\u798F\u3002\u662F\u554A\uFF0C\u5BF9\u4E8E\u6211\u4EEC\u201C\u5B66\u751F\u515A\u201D\u4EEC\u6765\u8BF4\uFF0C\u5E78\u798F\u771F\u7684\u662F\u4EF6\u5F88\u8F7B\u6613\u7684\u4E8B\u60C5\uFF1A\u7761\u68A6\u4E2D\u82CF\u9192\uFF0C\u4E00\u770B\u95F9\u949F\u624D\u534A\u591C3\u70B9\uFF0C\u8FD8\u80FD\u518D\u7761\u4E00\u4F1A\u7684\u611F\u89C9\uFF0C\u6BD4\u4EC0\u4E48\u90FD\u6EE1\u8DB3\u5FEB\u843D\uFF1B\u548C\u670B\u53CB\u5435\u67B6\u540E\u548C\u597D\uFF0C\u6709\u79CD\u91CD\u65B0\u62E5\u6709\u5168\u4E16\u754C\u7684\u9519\u89C9\uFF1B\u8FD8\u6709\u8001\u5E08\u7684\u4E00\u53E5\u8868\u626C\uFF0C\u7238\u5988\u7684\u4E00\u4EFD\u793C\u7269\u2026\u2026\u6211\u4EEC\u201C\u5B66\u751F\u515A\u201D\u751F\u6D3B\u4E2D\u5F88\u591A\u5C0F\u7EC6\u8282\uFF0C\u90FD\u4E0E\u5E78\u798F\u6302\u94A9\u3002\u611F\u53D7\u5B8C\u4EE5\u4E0B\u8FD9\u4E9B\u5355\u7EAF\u7F8E\u597D\uFF0C\u8C01\u8FD8\u613F\u610F\u957F\u5927\u5462\uFF1F</p><blockquote><p>\u56E2\u5BA0\u7684\u611F\u89C9\uFF0C\u4E5F\u53EA\u6709\u5B66\u751F\u65F6\u4EE3\u80FD\u62E5\u6709\u4E86\u4F60\u4E0A\u8BFE\u8D70\u795E\uFF0C\u8001\u5E08\u70B9\u4F60\u56DE\u7B54\u95EE\u9898\uFF0C\u5168\u73ED\u4EBA\u90FD\u5728\u544A\u8BC9\u4F60\u7B54\u6848\u3002</p></blockquote><blockquote><p>\u5BB9\u6613\u611F\u53D7\u5230\u5E78\u798F\u7684\u4EBA\uFF0C\u90FD\u6709\u4E00\u9897\u6E29\u67D4\u7684\u5FC3\u5427...</p><p>\u2714\u81EA\u5DF1\u7B14\u6389\u5230\u5730\u4E0A\uFF0C\u8DB4\u4E0B\u53BB\u6361\u7B14\u7684\u65F6\u5019\uFF0C\u53D1\u73B0\u540E\u684C\u7528\u624B\u5E2E\u6211\u6321\u4F4F\u4E86\u684C\u89D2\u3002</p><p>\u2714\u5F53\u81EA\u5DF1\u534A\u591C\u5199\u4F5C\u4E1A\uFF0C\u997F\u5230\u53D1\u660F\u65F6\uFF0C\u62C9\u5F00\u62BD\u5C49\uFF0C\u53D1\u73B0\u5F1F\u5F1F\u53C8\u5728\u6211\u7684\u62BD\u5C49\u91CC\u653E\u4E86\u4E00\u4E9B\u4ED6\u7684\u5C0F\u96F6\u98DF\u3002</p><p>\u2714\u4E0A\u8BFE\u4E0A\u4E00\u534A\u7684\u65F6\u5019\uFF0C\u6084\u6084\u8BF4\u4E86\u4E00\u53E5\u201C\u597D\u997F\u554A\u201D\uFF0C\u524D\u9762\u7684\u597D\u59D0\u59B9\u5C31\u4ECE\u5305\u5305\u638F\u51FA\u5C0F\u997C\u5E72\u7ED9\u6211\u5403\uFF0C\u771F\u7684\u592A\u5E78\u798F\u4E86\uFF01</p></blockquote><p><img src="https://www.hanjiaming.com.cn/wp-content/uploads/2020/07/20200725141042402.gif" alt="" loading="lazy"></p><blockquote><p>\u8003\u5DEE\u4E86\u6015\u4EC0\u4E48</p><p>\u8FD8\u6709\u4E00\u7FA4\u966A\u4F60\u7684\u5C0F\u4F19\u4F34</p><p>\u8003\u5DEE\u4E86\uFF0C\u8003\u5F97\u4E00\u584C\u7CCA\u6D82</p><p>\u6211\u54ED\u4E86\uFF0C\u54ED\u5F97\u7A00\u91CC\u54D7\u5566</p><p>\u539F\u672C\u8FD9\u9053\u9898\u505A\u5BF9\u4E86</p><p>\u53EF\u6211\u4E0D\u77E5\u9053\u600E\u4E48\u60F3\u7684</p><p>\u5728\u505A\u4E86\u4E00\u756A\u5FC3\u7406\u6597\u4E89\u540E</p><p>\u7EC8\u662F\u6539\u4E86\u7B54\u6848</p><p>\u4E00\u5F20\u5F20\u4F20\u9012\u7ED9\u6211\u7684\u7EB8\u6761</p><p>\u6211\u7D27\u7D27\u5730\u63E1\u5728\u624B\u5FC3\u91CC\uFF0C\u628A\u5B83\u63C9\u5F97\u76B1\u76B1\u5DF4\u5DF4</p><p>\u53C8\u54ED\u4E86</p><p>\u201C\u5F97\u5931\u5FC3\u4E0D\u8981\u592A\u91CD\u3002\u201D</p><p>\u201C\u4E0D\u8981\u592A\u6CE8\u91CD\u90A3\u4E2A\u5206\u6570\u3002\u201D</p><p>\u201C\u4F60\u770B\u4F60\u8003\u4E86\u4E5D\u5341\u591A\u5206\uFF0C\u6211\u624D\u8003\u516B\u5341\u591A\u5206\u5462\uFF01\u6211\u4EEC\u90FD\u4E00\u6837\u554A\u3002\u522B\u592A\u96BE\u8FC7\u5566\uFF01\u201D</p><p>\u8FD9\u8BA9\u6211\u611F\u53D7\u5230\u4E86\u6211\u7684\u540C\u5B66\u4EEC\u5FC3\u4E2D\u6709\u591A\u91CD\u8981</p><p>\u8FD9\u662F\u4E00\u79CD\u5E78\u798F</p></blockquote><blockquote><p>\u6BCF\u65E5\u4EFD\u7684\u5B89\u5FC3</p><p>\u7B80\u5355\u5B8C\u6210\u4E66\u684C\u4E0A\u5DF2\u7ECF\u5199\u5B8C\u7684\u4F5C\u4E1A\u3002</p></blockquote><p><img src="https://www.hanjiaming.com.cn/wp-content/uploads/2020/07/20200725141236994-1024x229.jpg" alt="" loading="lazy"></p><blockquote><p>\u25BC\u94C3\u58F0\u54CD\u8D77\uFF0C\u81EA\u7531\u4F11\u606F\uFF0C\u8001\u5E08\u7AD9\u4E86\u8D77\u6765\uFF0C\u201C\u4E0B\u8BFE\uFF01\u201D\u{1F44F}\u{1F44F}\u{1F44F}\u{1F44F}</p><p>\u25BC\u6696\u6696\u7684\uFF0C\u597D\u6CBB\u6108:\u6211\u8E72\u4E0B\u7CFB\u978B\u5E26\uFF0C\u4F60\u4EEC\u5728\u6211\u8EAB\u65C1\u4E5F\u8E72\u4E0B\u6765\uFF0C\u8BF4\u7B11\u7740\u7B49\u6211\u3002</p></blockquote><p><img src="https://www.hanjiaming.com.cn/wp-content/uploads/2020/07/20200725141151774.gif" alt="" loading="lazy"></p><blockquote><p>\u4E00\u70B9\u4E00\u6EF4\u7684\u5C0F\u8FDB\u6B65</p><p>\u5C31\u662F\u751F\u6D3B\u7ED9\u4E0E\u7684\u5C0F\u786E\u5E78</p><p>\u7EC8\u4E8E\u505A\u51FA\u4E00\u9053\u6570\u5B66\u9898</p><p>\u80CC\u4E86\u597D\u4E45\u7684\u6587\u8A00\u6587\uFF0C\u7B2C\u4E8C\u5929\u65E9\u4E0A\u7ADF\u7136\u8131\u53E3\u800C\u51FA</p><p>\u6BCF\u5929\u7EC3\u53E3\u8BED\uFF0C\u4E00\u5E74\u53D1\u73B0\u81EA\u5DF1\u53E3\u8BED\u4E00\u4E0B\u63D0\u5347\u4E86\u3002</p><p>\u25BC\u9694\u7740\u5C4F\u5E55\uFF0C\u90FD\u80FD\u611F\u53D7\u5230\uFF0C\u4E00\u9635\u6E05\u723D\u60A0\u626C\uFF0C\u7AD9\u5728\u697C\u9876\u5439\u5439\u98CE \u3002\u3002\u55EF\uFF0C\u5C31\u8FD9\u6837\u3002</p></blockquote><p><img src="https://www.hanjiaming.com.cn/wp-content/uploads/2020/07/20200725141319523.gif" alt="" loading="lazy"></p><p>\u5B66\u751F\u65F6\u4EE3\uFF0C\u7B80\u7B80\u5355\u5355\uFF0C\u5374\u662F\u6700\u96BE\u5F97\u7684\u5E78\u798F\u3002\u6709\u5FE7\u6101\u65F6\uFF0C\u53EA\u8981\u8E72\u5728\u95E8\u53E3\u5C0F\u5356\u90E8\uFF0C\u770B\u8001\u732B\u7761\u89C9\u7684\u6A21\u6837\uFF1B\u7AD9\u5728\u9633\u53F0\u7684\u7EFF\u841D\u4E0B\uFF0C\u770B\u85E4\u8513\u9759\u9759\u751F\u957F\uFF1B\u6216\u7EA6\u4E0A\u4E09\u4E94\u597D\u53CB\uFF0C\u4E00\u8D77\u6563\u6B65\u804A\u5929\uFF0C\u4FBF\u8DB3\u4EE5\u89E3\u9664\u70E6\u607C\u3002\u91CD\u65B0\u5F00\u5FC3\u5927\u7B11\uFF0C\u62E5\u62B1\u751F\u6D3B\u7684\u7F8E\u597D\u3002</p><p>\u53EA\u53EF\u60DC\u6709\u4EBA\u8BF4\uFF1A\u8D8A\u957F\u5927\uFF0C\u5C31\u8D8A\u5931\u53BB\u4E86\u611F\u53D7\u5E78\u798F\u7684\u80FD\u529B\u3002\u56E0\u4E3A\u968F\u7740\u5E74\u9F84\u7684\u589E\u957F\uFF0C\u70E6\u607C\u4E5F\u8D8A\u6765\u8D8A\u591A\u3002\u6E10\u6E10\u5730\uFF0C\u5F88\u591A\u4EBA\u90FD\u9057\u5931\u4E86\u4ECE\u524D\uFF0C\u5728\u6821\u56ED\u91CC\u7684\u90A3\u4EFD\u7B80\u5355\u4E0E\u7EAF\u7CB9\u3002\u4F46\u5F53\u6211\u4EEC\u6162\u6162\u957F\u5927\uFF0C\u89C9\u5F97\u8D8A\u6765\u8D8A\u96BE\u5FEB\u4E50\u65F6\uFF0C\u4E0D\u59A8\u56DE\u5FC6\u4E00\u4E0B\u8FD9\u4E9B\u66FE\u7ECF\u7684\u65E5\u5B50\uFF0C\u627E\u56DE\u7231\u7B11\u7684\u521D\u5FC3\u3002\u5F88\u559C\u6B22\u4E00\u53E5\u8BDD\uFF1A\u751F\u6D3B\u662F\u7410\u788E\u7684\uFF0C\u751F\u547D\u5374\u4E0D\u80FD\u7410\u788E\u3002\u6211\u4EEC\u8981\u7CBE\u5FC3\u64E6\u62ED\uFF0C\u8BA9\u5B83\u53D1\u5149\u3002\u65E0\u8BBA\u4F55\u65F6\uFF0C\u90FD\u8981\u8BA9\u81EA\u5DF1\u52AA\u529B\u611F\u53D7\u5E78\u798F\uFF01</p><p><img src="https://www.hanjiaming.com.cn/wp-content/uploads/2020/07/20200725141621601.gif" alt="" loading="lazy"></p>',16);function c(n,l){return a}var i=p(t,[["render",c]]);export{i as default};
